<?phpclass Model {    use \Iris\BaseModel;    protected $fields = array(        "Id" => array("type" => "string"),        "Status" => array("type" => "\Iris\Status")    );    public function __construct($data) {        $this->set_data($data);    }}class ArrModel {    use \Iris\BaseModel;    protected $fields = array(        "Item" => array("type" => "ItemModel")    );    public function __construct($data) {        $this->set_data($data);    }}class ItemModel {    use \Iris\BaseModel;    protected $fields = array(        "Phone" => array("type" => "string")    );    public function __construct($data) {        $this->set_data($data);    }}class BaseModelTest extends PHPUnit_Framework_TestCase {	public function setUp() {    }    public function testContent() {        $this->model = new Model(array("Id" => "123", "Status" => array("Code" => "0", "Description" => "Empty")));        $this->assertEquals("123", $this->model->Id);        $this->assertEquals("0", $this->model->Status->Code);        $this->model->set_data(array("Id" => "222", "Status" => array("Code" => "200", "Description" => "Hello")));        $this->assertEquals("222", $this->model->Id);        $this->assertEquals("200", $this->model->Status->Code);    }    public function testArray() {        $this->arrModel = new ArrModel(["Item" => [ ["Phone" => "1"], ["Phone" => "2"] ]]);        $this->assertEquals("1", $this->arrModel->Item[0]->Phone);        $arr = $this->arrModel->to_array();        $this->assertEquals("1", $arr["Item"][0]["Phone"]);    }}